import React, { useState } from "react";
import { useBasket } from "../../contexts/BasketContext";
import { Alert, Image, Button, Box, Text, Textarea } from "@chakra-ui/react";
import { Link } from "react-router-dom";
import styles from "./styles.module.css";

import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
  FormControl,
  FormLabel,
} from "@chakra-ui/react";
import { postOrder } from "../../api";

function Basket() {
  const [address, setAddress] = useState("");

  const { isOpen, onOpen, onClose } = useDisclosure();
  const initialRef = React.useRef();
  const finalRef = React.useRef();

  const { items, removeFromBasket, emptyBasket } = useBasket();
  console.log("baseketitesm", items);

  const handleSubmitForm = async () => {
    const itemIds = items.map((item) => item.id);

    const input = {
      address,
      items: JSON.stringify(itemIds),
    };

    const response = await postOrder(input);
    console.log("response", response);

    emptyBasket();
    onClose();

    console.log("itemIds", itemIds);
  };

  const total = items.reduce((acc, obj) => acc + obj.price, 0);

  return (
    <Box p="10">
      {items.length < 1 && (
        <Alert status="warning">You have not any items in your basket</Alert>
      )}

      {items.length > 0 && (
        <>
          <ul className={styles.basketul}>
            {items.map((item) => (
              <li key={item.id} className={styles.basketli}>
                <Link to={`/product/${item.id}`} className={styles.basketlink}>
                  <Image htmlWidth={100} src={item.image} alt="basket item" />
                  <p>{item.title}</p>
                  <p>{item.price} ₺</p>
                </Link>
                <Button
                  className={styles.basketremovebtn}
                  mt="2"
                  size="sm"
                  colorScheme="pink"
                  onClick={() => removeFromBasket(item.id)}
                >
                  Remove
                </Button>
              </li>
            ))}
          </ul>
          <Box mt="10">
            <Text fontSize="22">Total: {total} ₺</Text>
          </Box>

          <Button mt="2" onClick={onOpen}>
            Order
          </Button>

          <Modal
            initialFocusRef={initialRef}
            finalFocusRef={finalRef}
            isOpen={isOpen}
            onClose={onClose}
          >
            <ModalOverlay />
            <ModalContent>
              <ModalHeader>Order</ModalHeader>
              <ModalCloseButton />
              <ModalBody pb={6}>
                <FormControl>
                  <FormLabel>Address</FormLabel>
                  <Textarea
                    ref={initialRef}
                    placeholder="Enter your address here..."
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                  />
                </FormControl>
              </ModalBody>

              <ModalFooter>
                <Button colorScheme="blue" mr={3} onClick={handleSubmitForm}>
                  Save
                </Button>
                <Button onClick={onClose}>Cancel</Button>
              </ModalFooter>
            </ModalContent>
          </Modal>
        </>
      )}
    </Box>
  );
}

export default Basket;
