import React from "react";
import { useBasket } from "../../contexts/BasketContext";
import { Alert, Image, Button, Box, Text, flexbox } from "@chakra-ui/react";
import { Link } from "react-router-dom";
import styles from "./styles.module.css";

function Basket() {
  const { items, removeFromBasket } = useBasket();

  const total = items.reduce((acc, obj) => acc + obj.price, 0);

  return (
    <Box p="10">
      {items.length < 1 && (
        <Alert status="warning">You have not any items in your basket</Alert>
      )}

      {items.length > 0 && (
        <>
          <ul>
            {items.map((item) => (
              <li key={item.id} className={styles.basketli}>
                <Link to={`/product/${item.id}`}>
                  <Image htmlWidth={100} src={item.image} alt="basket item" />
                  {item.title} - {item.price} ₺
                </Link>
                <Button mt="2" size="sm" colorScheme="pink" onClick={()=> removeFromBasket(item.id)}>
                  Remove from Basket
                </Button>
              </li>
            ))}
          </ul>
          <Box mt="10">
            <Text fontSize="22">Total: {total} ₺</Text>
          </Box>
        </>
      )}
    </Box>
  );
}

export default Basket;
